var imagens = new Array();
var descricoes = new Array();
var precos = new Array();
var materiaPrima = new Array();
var tempoEstimado = new Array();

$(document).ready(function(){
    // the "href" attribute of the modal trigger must specify the modal ID that wants to be triggered
    $('.modal').modal();
    $('select').material_select();
    
    for (let i = 1; i <= 3; i++) {
        sumirImagem(i);    
        
        document.getElementsByClassName("materiasPrimas")[i-1].innerHTML = "<h5>Materia-prima a comprar</h5>"+materiaPrima[i].nome[0]+": R$"+materiaPrima[i].preco[0]+"<br>"+materiaPrima[i].nome[1]+": R$"+materiaPrima[i].preco[1];
         
	    document.getElementsByClassName("tempoEstimado")[i-1].innerHTML = "<h5>Tempo estimado</h5>"+tempoEstimado[1].nome[0]+": "+tempoEstimado[1].tempo[0]+"<br>"+tempoEstimado[1].nome[1]+": "+tempoEstimado[1].tempo[1]+"<br>"+tempoEstimado[1].nome[2]+": "+tempoEstimado[1].tempo[2]+"<hr>"+tempoEstimado[1].nome[3]+": "+tempoEstimado[1].tempo[3];
    }
});

for (let i = 1; i <= 6; i++) {
	imagens[i, 1] = "data:image/jpeg;base64,";
	imagens[i, 2] = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTUeq1tTwbrPw19Px3eG5pc-ePGNrVv7A4yn-R-yawSjp77sz1C";
	imagens[i, 3] = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTTVrVnLCISsvy1hrsrbZ3ZMmbHsMyExTEECplPcZrCJLEOdGwukw";
	imagens[i, 4] = "data:image/jpeg;base64,";
	imagens[i, 5] = "data:image/jpeg;base64,";
	imagens[i, 6] = "http://megario.vteximg.com.br/arquivos/ids/156486/M-MS-chocolate-POUCH-200G.jpg";
	
	descricoes[i, 1] = "Isso  um pote";
	descricoes[i, 2] = "Isso  um potinho";
	descricoes[i, 3] = "Isso  um latinha";
	descricoes[i, 4] = "Isso  uma caixa de cereal";
	descricoes[i, 5] = "Isso  uma embalagem de massa de tomate";
	descricoes[i, 6] = "Isso  um pacote de chocolate";
	
	precos[i, 1] = 5.00;
	precos[i, 2] = 6.00;
	precos[i, 3] = 7.00;
	precos[i, 4] = 8.00;
	precos[i, 5] = 9.00;
	precos[i, 6] = 10.00;
	
	materiaPrima[i] = {
		"nome": ["PET", "Polipropileno"],
		"preco": [(20.00 * i), (40.00 * i)]
	}
	
	tempoEstimado[i] = {
		"nome": ["Importar matria-prima", "Produo", "Envio", "Total"],
		"tempo": ["3 dias", "4 dias", "5 anos", "5 anos e 7 dias"]
	}
}

function precoTotal(i) {
	var total = 0.0;
	for (let a = 1; a <= 6; a++)
		total += precos[i, a] * 5;
	return total;
}

function mostrarImagem(i, j) {
	document.getElementsByClassName("imagemProduto")[i-1].style.backgroundImage = "url('" + imagens[i, j] + "')";
	document.getElementsByClassName("descricaoProduto")[i-1].innerHTML = "Descrio: " + descricoes[i, j] + "<br>Quantidade: 5 unidades" ;
	document.getElementsByClassName("nomeProduto")[i-1].innerHTML = document.getElementsByClassName("item")[j-1].innerHTML;
	document.getElementsByClassName("preco-unitario")[i-1].innerHTML = "Preo unitrio: R$" + precos[i, j];
	document.getElementsByClassName("preco-total")[i-1].innerHTML = "Preo total: R$" + (precos[i, j] * 5);
	document.getElementsByClassName("custo-producao")[i-1].innerHTML = "Produo: R$150";
}

function sumirImagem(i) {
	document.getElementsByClassName("imagemProduto")[i-1].style.backgroundImage = "url('source/empresa/Controle de Pedidos - Produo/Libri.jpg')";
	document.getElementsByClassName("descricaoProduto")[i-1].innerHTML = "" ;
	document.getElementsByClassName("nomeProduto")[i-1].innerHTML = "Shulambs";
	document.getElementsByClassName("preco-unitario")[i-1].innerHTML = "";
	document.getElementsByClassName("preco-total")[i-1].innerHTML = "Preo total: R$" + precoTotal(i);
	document.getElementsByClassName("custo-producao")[i-1].innerHTML = "Produo: R$900";
}